#need to know how to read these text files properly - good place to start
id = ['Rosalind_0069', 'Rosalind_4130', 'Rosalind_9266', 'Rosalind_8338', 'Rosalind_2780']
st = ['', '', '', '', '']
content = []

for i in st:
    cont = 0
    for x in i:
        if x == 'G' or x == 'C':
            cont += 1
    content.append((cont/len(i))*100)


pos = 0 #determine position within each array
mx = content[0]
for y in content:
    if y > mx:
        mx = y
    pos = content.index(mx) #getting the index in the array


print(id[pos], content[pos])
